# Structure for table "submit_custom"
#

DROP TABLE IF EXISTS `submit_custom`;
CREATE TABLE `submit_custom` (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id名',
  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '姓名',
  `telphone` varchar(20) NOT NULL DEFAULT '' COMMENT '电话',
  `dates` int(11) NOT NULL DEFAULT '0' COMMENT '出发日期',
  `days` int(11) NOT NULL DEFAULT '0' COMMENT '出行天数',
  `person_count` int(11) NOT NULL DEFAULT '0' COMMENT '出行人数',
  `email` varchar(32) NOT NULL DEFAULT '' COMMENT '邮箱',
  `contact_time` varchar(32) DEFAULT '' COMMENT '联系时间',
  `type_team` varchar(32) DEFAULT NULL COMMENT '团队类型',
  `active_content` text COMMENT '活动内容',
  `citys` varchar(32) DEFAULT NULL COMMENT '出发城市',
  `purpose` varchar(32) DEFAULT NULL COMMENT '目的地',
  `budget` int(11) DEFAULT NULL COMMENT '单人预算',
  `other` text COMMENT '其他',
  `team_theme` varchar(32) DEFAULT NULL COMMENT '团建主题',
  `hotel` varchar(32) DEFAULT NULL COMMENT '住宿类型',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='定制表';

